const availabilityOptions = ['Weekdays','Weekends','Evenings','Mornings','Flexible'];

function getCategoryFromSkill(skill) {
  if ([
    'Mathematics','Physics','Chemistry','Biology','English Literature','History','Geography','Economics','Accounting','Statistics','Computer Science'
  ].includes(skill)) return 'Academic';
  if ([
    'Python Programming','Java Programming','C++ Programming','JavaScript','React Development','Node.js','Data Structures & Algorithms','Machine Learning','Data Science','AI/Deep Learning','DevOps','Cloud (AWS)','Cybersecurity','Android Development','iOS Development'
  ].includes(skill)) return 'Programming & Tech';
  if ([
    'Guitar Lessons','Piano Lessons','Violin','Singing/Vocals','Drums','Music Production'
  ].includes(skill)) return 'Music';
  if ([
    'Hindi','Kannada','Tamil','Telugu','Spanish','French','German','Japanese','Mandarin','Arabic'
  ].includes(skill)) return 'Languages';
  if ([
    'Yoga & Wellness','Fitness Training','Cricket Coaching','Football Coaching','Tennis Coaching','Badminton Coaching','Swimming'
  ].includes(skill)) return 'Sports & Fitness';
  if ([
    'Drawing & Sketching','Painting (Acrylic/Watercolor)','Photography','Graphic Design','UI/UX Design','Video Editing'
  ].includes(skill)) return 'Arts & Design';
  if ([
    'SAT Prep','GRE Prep','GMAT Prep','IELTS Prep','TOEFL Prep','JEE Mains','NEET Prep','CAT Prep'
  ].includes(skill)) return 'Test Prep';
  return 'Vocational & Others';
}

const basePriceByCategory = {
  'Academic': 900,
  'Programming & Tech': 2000,
  'Music': 1200,
  'Languages': 1000,
  'Sports & Fitness': 1100,
  'Arts & Design': 1000,
  'Test Prep': 2500,
  'Vocational & Others': 800,
};

// Simple deterministic pseudo-random number generator (LCG)
function createRng(seed) {
  let s = seed >>> 0;
  return function rand() {
    // LCG constants
    s = (s * 1664525 + 1013904223) >>> 0;
    return s / 0xFFFFFFFF;
  };
}

function pick(arr, rand) {
  return arr[Math.floor(rand() * arr.length) % arr.length];
}

function rupee(n) {
  const rounded = Math.round(n / 50) * 50; // round to nearest 50
  return `â‚¹${rounded}/hr`;
}

function buildDescription(name, skill, years, location) {
  const intro = `Experienced ${skill.toLowerCase()} tutor with ${years}+ years of teaching.`;
  const style = `Personalized, outcome-focused sessions designed around your goals.`;
  const loc = `Available in ${location} and online.`;
  return `${intro} ${style} ${loc}`;
}

function generateTutors(count = 1500, seed = 42) {
  const rand = createRng(seed);
  const arr = [];
  for (let i = 1; i <= count; i++) {
    const first = pick(firstNames, rand);
    const last = pick(lastNames, rand);
    const name = `${first} ${last}`;

    const skill = pick(skills, rand);
    const category = getCategoryFromSkill(skill);
    const location = pick(locations, rand);
    const years = 1 + Math.floor(rand() * 15);
    const rating = Math.round((3.5 + rand() * 1.5) * 10) / 10; // 3.5 - 5.0

    const base = basePriceByCategory[category] || 1000;
    const priceNum = base + Math.floor(rand() * base * 1.2);
    const price = rupee(priceNum);

    const color = colors[i % colors.length];
    const availability = pick(availabilityOptions, rand);
    const description = buildDescription(name, skill, years, location);

    arr.push({
      id: i,
      name,
      skill,
      category,
      location,
      price,
      color,
      rating,
      experience: years,
      availability,
      description,
    });
  }
  return arr;
}

export const tutors = generateTutors();

export function getAllTutors() {
  return tutors;
}

export function getTutorById(id) {
  const n = typeof id === 'string' ? parseInt(id, 10) : id;
  return tutors.find(t => t.id === n);
}

export function searchTutors({ q = '', skill = 'All', location = 'All' } = {}) {
  const query = q.trim().toLowerCase();
  return tutors.filter(t => {
    const matchesQuery = query === '' ||
      t.name.toLowerCase().includes(query) ||
      t.skill.toLowerCase().includes(query) ||
      t.location.toLowerCase().includes(query) ||
      (t.category && t.category.toLowerCase().includes(query));

    const matchesSkill = skill === 'All' || t.skill === skill || (t.category === skill);
    const matchesLocation = location === 'All' || t.location === location;

    return matchesQuery && matchesSkill && matchesLocation;
  });
}
